(function initHeader() {
  const container = document.getElementById('layout-header');
  if (container && !container.dataset.headerInjected) {
    container.innerHTML = HEADER_HTML;
    container.dataset.headerInjected = 'true';
  }

  if (!document.getElementById('layout-header-css')) {
    const styleEl = document.createElement('style');
    styleEl.id = 'layout-header-css';
    styleEl.textContent = HEADER_CSS;
    document.head.appendChild(styleEl);
  }
})();

function getAdminCategoriesFromStorage() {
  try {
    // 1. Нормальный путь через ShopData
    if (window.ShopData && typeof window.ShopData.getCategories === 'function') {
      return window.ShopData.getCategories();
    }

    // 2. Фоллбек напрямую
    const rawSnake = (typeof window !== 'undefined' && window.localStorage)
      ? localStorage.getItem('staff_categories')
      : null;

    const rawCamel = (typeof window !== 'undefined' && window.localStorage)
      ? localStorage.getItem('staffCategories')
      : null;

    const raw = rawSnake || rawCamel;
    if (!raw) return [];

    const parsed = JSON.parse(raw);

    // если это просто массив категорий
    if (Array.isArray(parsed)) return parsed;

    if (parsed && Array.isArray(parsed.categories)) return parsed.categories;

    return [];
  } catch (err) {
    console.warn('Помилка читання категорій для хедера:', err);
    return [];
  }
}

function renderHeaderDynamicCategories() {
  const megaInner = document.querySelector('.mega-categories .mega-inner');
  if (!megaInner) return;

  megaInner.innerHTML = '';

  const categories = getAdminCategoriesFromStorage();

  // Если админ ещё ничего не создал
  if (!categories || !categories.length) {
    const col = document.createElement('div');
    col.className = 'mega-col';
    col.innerHTML = `
      <div class="mega-group">
        <div class="mega-heading">Категорії поки що не створені</div>
        <div class="mega-link">
          Додай першу категорію у розділі "Адмінка → Модерація товарів".
        </div>
      </div>
    `;
    megaInner.appendChild(col);
    return;
  }

  // Сортировка красиво по имени
  const sorted = categories.slice().sort((a, b) => {
    const aName = (a.name || a.title || a.code || '').toLowerCase();
    const bName = (b.name || b.title || b.code || '').toLowerCase();
    return aName.localeCompare(bName, 'uk');
  });

  // Усі категорії
  const col = document.createElement('div');
  col.className = 'mega-col';

  const group = document.createElement('div');
  group.className = 'mega-group';

  const heading = document.createElement('a');
  heading.href = '#';
  heading.className = 'mega-heading';
  heading.textContent = 'Усі категорії';
  group.appendChild(heading);

  sorted.forEach(cat => {
    const slug = cat.code || cat.slug || String(cat.id || '').trim();
    if (!slug) return;

    const link = document.createElement('a');
    link.className = 'mega-link';
    link.textContent = cat.name || cat.title || slug;
    // ВАЖНО: параметр slug, не category
    link.href = `category.html?slug=${encodeURIComponent(slug)}`;

    group.appendChild(link);
  });

  col.appendChild(group);
  megaInner.appendChild(col);
}

(function () {
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', renderHeaderDynamicCategories);
  } else {
    renderHeaderDynamicCategories();
  }
})();
